export const ALL_PUZZLE_PIECES: PuzzlePieceId[] = PUZZLES.flatMap(puzzle => {
  const pieces: PuzzlePieceId[] = [];
  for (let r = 0; r < puzzle.rows; r++) {
    for (let c = 0; c < puzzle.cols; c++) {
      pieces.push(`${puzzle.id}_${r}_${c}`);
    }
  }
  return pieces;
});


